  @UnitTest
  void getCommandForSqlWithInsertContainerPatternReturnInstanceOfInsertTableCommand() {
    PlaceholderData placeholderData = new PlaceholderData()
        .set(1, "0").set(2, 0L).set(3, 0).set(4, 0)
        .set(5, 0L).set(6, 0L).set(7, 0L).set(8, 0)
        .set(9, 0L).set(10, new byte[0]).set(11, new byte[0]);
    Command commandObj = new CommandFactory().getCommandForSql(
        "insert into SETTINGS(ID, MODIFIED, HASBINARY, DELETEDONCE, MODCOUNT, CMODCOUNT, DSIZE, VERSION, SDTYPE, SDMAXREVTIME, DATA, BDATA) values (?, ?, ?, ?, ?, ?, ?,  2, ?, ?, ?, ?)",
        placeholderData,
        Integer.MAX_VALUE
    );

    assertThat(commandObj, is(instanceOf(InsertIntoContainerCommand.class)));
  }

  @UnitTest
  void getCommandForSqlWithUpdatePatternReturnsInstanceOfUpdateCommand() {
    PlaceholderData placeholderData = new PlaceholderData()
        .set(1, 0L).set(2, 0L).set(3, 0).set(4, 0)
        .set(5, 0L).set(6, 0L).set(7, 0L).set(8, "0")
        .set(9, 0L).set(10, "").set(11, 0L);
    Command commandObj = new CommandFactory().getCommandForSql(
        "update CLUSTERNODES set MODIFIED = case when ? > MODIFIED then ? else MODIFIED end, HASBINARY = ?, DELETEDONCE = ?, MODCOUNT = ?, CMODCOUNT = ?, DSIZE = DSIZE + ?, VERSION = 2, DATA = DATA || CAST(? AS varchar(16384)) where ID = ? and MODCOUNT = ?",
        placeholderData,
        Integer.MAX_VALUE
    );

    assertThat(commandObj, is(instanceOf(UpdatDocumentDataInContainerCommand.class)));
  }

  @UnitTest
  void getCommandForSqlWithSelectIdFromContainerPatternReturnInstanceOfSelectFromContainerByIdCommand() {
    Command commandObj = new CommandFactory().getCommandForSql("select * from CLUSTERNODES where ID = '0'");

    assertThat(commandObj, is(instanceOf(SelectFromContainerByIdCommand.class)));
  }

  void getCommandForSqlWithNullReturnErrorCommand() {
    Command commandObj = new CommandFactory().getCommandForSql(null);

    assertThat(commandObj, is(instanceOf(ErrorCommand.class)));
  }

